import {React,useEffect, useState} from 'react';
import '../../App.css';
import {Route, Redirect, useHistory} from 'react-router-dom'
import Modal from 'react-modal';
import { useForm } from "react-hook-form";
import { login, createUser } from "../../functions/users";
import ReactDOM from 'react-dom';


/*Signed up successfully modal style*/
const customStyles = {
    content : {
      top                   : '50%',
      left                  : '50%',
      right                 : 'auto',
      bottom                : 'auto',
      marginRight           : '-50%',
      transform             : 'translate(-50%, -50%)',
      backgroundColor       : "#d3f1ef",
      position              : "fixed",
      width: "300px",
      height: "100px",
    }
  };


function SignUpPre() {

    const history = useHistory();
    const [modalIsOpen,setIsOpen] = useState(false);
    const [errorModalIsOpen,setErrorModalIsOpen] = useState(false);
    // const [worngEmailModalIsOpen,setWorngEmailModalIsOpen] = useState("");

    const { register, handleSubmit } = useForm();

    const [passFInput,setPassFInput] = useState("");
    const [passSInput,setPassSInput] = useState("");
    const [userEmail,setUserEmail] = useState("");

    const [correctMail, setCorrectMail] = useState("");
    // var correctMail = false;


    const onSubmit = async (data) => {
      console.log(userEmail);
      console.log(userEmail.split("@"));
      var endEmail = userEmail.split("@")[1];
      console.log(endEmail);
      if (endEmail == 'mail.tau.ac.il'){
        setCorrectMail(true);
        // correctMail = true;
      }
      else{
        setCorrectMail(false);
        // correctMail = false;
      }
      console.log(correctMail);
      if (passFInput == passSInput && correctMail){
        var reponse = await createUser(data);
        console.log(reponse);
      }
      // var reponse = await createUser(data);
      // console.log(reponse);
    };


  function Redirect(page){

    history.push("/"+page);
    return <Redirect    to={{
      pathname:'/'+page
        }}/>
  }
  function openModal(){
    setIsOpen(passFInput == passSInput);
    setErrorModalIsOpen(passFInput != passSInput);
    // setWorngEmailModalIsOpen(!correctMail);



}


  function closeModal(){
    setIsOpen(false);
    setErrorModalIsOpen(false);
    setWorngEmailModalIsOpen(false);
  }

  return (
    <div className="Home">
      <div className="login" >
        <p staly={{marginBottom: '30px'}}>PartnerTAU ברוכים הבאים להרשמה לאתר</p>
        <div className="col" >
        <form onSubmit={handleSubmit(onSubmit)}>
            <div className="row" style={{width: '100%'}}>
              <input className="inputclass" type ="text"></input>
              <div>שם פרטי</div>
            </div>
            <div className="row" style={{width: '100%'}}>
            <input className="inputclass" type ="text"></input>
              <div>שם משפחה</div>
            </div>

            <div className="row" style={{width: '100%'}}>
              
              <input className="inputclass" type ="email" {...register("username")} onChange={(w) =>{setUserEmail(w.target.value)}} ></input>
              <div>מייל</div>
            </div>

            <div className="row" style={{width: '100%'}}>
            <input id="passF" className="inputclass" type ="password" maxLength = "10" {...register("password")} 
            onChange={(w) =>{setPassFInput(w.target.value)}} ></input>
              <div>סיסמה</div>
            </div>
            <div className="row" style={{width: '100%'}}>
            <input id="passS" className="inputclass" type ="password" maxLength = "10"
            onChange={(w) =>{setPassSInput(w.target.value)}} ></input>
              <div>אימות סיסמה</div>
            </div>

            <button type="submit" onClick={()=>{openModal()}} className="button button1">הירשם</button>
        </form>
        </div>
      </div>
      <Modal
          isOpen={modalIsOpen}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
        <div className="Modal">
            <div>נרשמת בהצלחה</div>
            <button className="button button1" onClick={()=>{Redirect('Home')}}>חזור לדף הבית</button>
        </div>
        </Modal>
        <Modal
          isOpen={errorModalIsOpen}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
        <div className="Modal">
            <div>סיסמאות לא זהות</div>
            <button className="button button1" onClick={closeModal}>נסה שוב</button>
        </div>
        </Modal>
        <Modal
          isOpen={correctMail}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
        <div className="Modal">
            <div>כתובת מייל לא אונברסטאית</div>
            <button className="button button1" onClick={closeModal}>נסה שוב</button>
        </div>
        </Modal>
    </div>
  );
}

export default SignUpPre;
