import logo from '../../images/logo.png';
import {React,useEffect, useState} from 'react';
import '../../App.css';
import {Route, Redirect, useHistory} from 'react-router-dom'
import Login from "../loginpage";
import { loginConfirmed } from "../../utils/subjects/loginSubject/loginSubject";

import Modal from 'react-modal';

const customStyles = {
    content : {
      top                   : '50%',
      left                  : '50%',
      right                 : 'auto',
      bottom                : 'auto',
      marginRight           : '-50%',
      transform             : 'translate(-50%, -50%)',
      backgroundColor: "#d3f1ef",
    }
  };


  function NavBar()
  {

    useEffect(() => {
        const subscriptionCC =  loginConfirmed.onSubjectTrigged()
      
      .subscribe( () => {
        setIsOpen(false);
        setUsername(localStorage.getItem('user'))


      });
      
    }, [])

    const history = useHistory();
    const [modalIsOpen,setIsOpen] = useState(false);
    const [buttonString,setButtonString] = useState("");
    const [username,setUsername] = useState("");
  
    function openModal(){
      setIsOpen(true);
  }
  
  
    function closeModal(){
      setIsOpen(false);
    }
  
    function Redirect(page){
  
      history.push("/"+page);
      return <Redirect    to={{
        pathname:'/'+page
          }}/>
    }

    function signOut(){
        if(localStorage.getItem('user'))//Connected
        {
            localStorage.removeItem('user')
            setUsername('')
        }
        

    }

    return (
        <div className="Home">
          <div className="Top">
            <div>
                {username} && 
              <button onClick={openModal} className="button button1">{username && username != '' ? 'התנתק' : 'התחבר'}</button>
              <button onClick={()=>{Redirect('List')}} className="button button1">רשימת הבקשות שלי</button>
              <button onClick={()=>{Redirect('Home')}} className="button button1">דף הבית</button>
            </div>
            <div className="row">
              <div>{username && username != '' ? username : ''}</div>
              <img src={logo} alt="Logo"/>
            </div>
          </div>

          <Modal
          isOpen={modalIsOpen}
          style={customStyles}
          contentLabel="Example Modal"
        >
               <Login/>
        </Modal>

        </div>
      );



  }


  export default NavBar;
