import logo from "./logo.png";
import { React, useEffect, useState } from "react";
import "../../App.css";
import { Route, Redirect, useHistory } from "react-router-dom";
import { loginConfirmed } from "../../utils/subjects/loginSubject/loginSubject";
import {SignIn} from "../../utils/subjects/users/usersService"

/*import Modal from 'react-modal';


const customStyles = {
  content : {
    top                   : '50%',
    left                  : '50%',
    right                 : 'auto',
    bottom                : 'auto',
    marginRight           : '-50%',
    transform             : 'translate(-50%, -50%)',
    backgroundColor: "rgb(42,95,158)"
  }
};*/

function Login() {
  const [usernameInput,setUsernameInput] = useState("");
  const [passwordInput,setPasswordInput] = useState("");

  async function Log() {
    if (localStorage.getItem("user")) {
      //Connected
      localStorage.removeItem("user");
      localStorage.removeItemem("token");
    } else {

      var response = await SignIn(usernameInput,passwordInput)
    //Answer always in .data
      if(response.data && response.token)
      {
        localStorage.setItem("user", usernameInput);
        localStorage.setItem("token", response.token);
      }
      else
      {
        alert("An error occourd!")
      }

    }
    loginConfirmed.subjectTrigger();
  }

  return (
    <div className="Home">
      <div className="login">
        <p staly={{ marginBottom: "30px" }}>PartnerTAU ברוכים הבאים לאתר</p>
        <div>
          <input onChange={(w) =>{setUsernameInput(w.target.value)}} type="text"></input>שם משתמש
        </div>
        <div>
          <input onChange={(w) =>{setPasswordInput(w.target.value)}} type="text" style={{ marginBottom: "30px" }}></input>סיסמה
        </div>
        <div className="Row">
          <button className="button button1">שכחתי סיסמה</button>
          <button className="button button1">הירשם</button>
          <button onClick={Log} className="button button1">
            התחבר
          </button>
        </div>
      </div>
    </div>
  );
}

export default Login;
