import {React,useEffect, useState} from 'react';
import '../../App.css';
import {Route, Redirect, useHistory} from 'react-router-dom'
import Modal from 'react-modal';
import { useForm } from "react-hook-form";
import { createUser } from "../../functions/users";
import ReactDOM from 'react-dom';

/*Signed up successfully modal style*/
const customStyles = {
    content : {
      top                   : '50%',
      left                  : '50%',
      right                 : 'auto',
      bottom                : 'auto',
      marginRight           : '-50%',
      transform             : 'translate(-50%, -50%)',
      backgroundColor       : "#d3f1ef",
      position              : "fixed",
      width: "300px",
      height: "100px",
    }
  };


function SignUpPre() {

    const history = useHistory();
    const [modalIsOpen,setIsOpen] = useState(false);
    const [username, setUsername] = useState(""); 

    const { register, handleSubmit } = useForm();

    const [passFInput,setPassFInput] = useState("");
    const [passSInput,setPassSInput] = useState("");

    const onSubmit = async (data) => {
      // console.log(passFInput);
      // console.log(passSInput);
      if (passFInput == passSInput){
        var reponse = await createUser(data);
        console.log(reponse);
      }
      // var reponse = await createUser(data);
      // console.log(reponse);
    };

  function Redirect(page){

    history.push("/"+page);
    return <Redirect    to={{
      pathname:'/'+page
        }}/>
  }
  function openModal(){
    setIsOpen(true);
}


  function closeModal(){
    setIsOpen(false);
  }

  return (
    <div className="Home">
      <div className="login" >
        <p staly={{marginBottom: '30px'}}>PartnerTAU ברוכים הבאים להרשמה לאתר</p>
        <div className="col" >
        <form onSubmit={handleSubmit(onSubmit)}>
            <div className="row" style={{width: '100%'}}>
              <input className="inputclass" type ="text"></input>
              <div>שם פרטי</div>
            </div>
            <div className="row" style={{width: '100%'}}>
            <input className="inputclass" type ="text"></input>
              <div>שם משפחה</div>
            </div>
            <div className="row" style={{width: '100%'}}>
              <input className="inputclass" type ="text" style={{width: '200px'}} {...register("username")} ></input>
              <div>מייל</div>
            </div>
            <div className="row" style={{width: '100%'}}>
            <input id="passF" className="inputclass" type ="password" maxLength = "10" {...register("password")} 
            onChange={(w) =>{setPassFInput(w.target.value)}} ></input>
              <div>סיסמה</div>
            </div>
            <div className="row" style={{width: '100%'}}>
            <input id="passS" className="inputclass" type ="password" maxLength = "10"
            onChange={(w) =>{setPassSInput(w.target.value)}} ></input>
              <div>אימות סיסמה</div>
            </div>
            <select name="pets" id="pet-select" onChange={(w) =>{setEmailInput(w.target.value)}}>
              <option value="">--Please choose an option--</option>
              <option value="@mail.tau.ac.il">@mail.tau.ac.il</option>
              <option value="@gmail">@gmail</option>
            </select>
            <button type="submit" onClick={()=>{openModal()}} className="button button1">הירשם</button>
        </form>
        </div>
      </div>
      <Modal
          isOpen={modalIsOpen}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
        <div className="Modal">
            <div>נרשמת בהצלחה</div>
            <button className="button button1" onClick={()=>{Redirect('Home')}}>חזור לדף הבית</button>
        </div>
        </Modal>
    </div>
  );
}

export default SignUpPre;
