import axios from "axios"

axios.defaults.headers.common['Authorization'] = "Bearer " + (localStorage.getItem('token') ?localStorage.getItem('token') : "" );


export const  getOpenRequests = () =>{

    return axios.get('https://localhost:3001/GetActiveRequestedByUserId')
    .then(function (response) {
  
      return response.data
      
    
    })
    .catch(function (error) {
       
        return error
    })
    

}

export const  getClosedRequests = () =>{

    return axios.get('https://localhost:3001/CreditCard/GetCreditCards')
    .then(function (response) {
  
      return response.data
      
    
    })
    .catch(function (error) {
       
        return error
    })
    

}
