const axios = require('axios');


export const  login = async (params) =>{
    //option 2 for async/await handling
    const resp = await axios.post('http://localhost:3001/login', params);
    console.log(resp);
    if(resp)
    {
        localStorage.setItem('token',resp.data)
    }
    return resp;
}

export const  createUser = async (params) =>{
    //option 2 for async/await handling
    const resp = await axios.post('http://localhost:3001/createUser', params);

    firebase.sendSignInLinkToEmail(params.email, actionCodeSettings)
