const axios = require('axios');


export const  getUser = async (params) =>{
    //option 2 for async/await handling
    const resp = await axios.post('http://localhost:3001/getUser', params);
    console.log(resp);
    return resp;
}

export const  createUser = async (params) =>{
    //option 2 for async/await handling
    const resp = await axios.post('http://localhost:3001/createUser', params);
    console.log(resp);
    return resp;
}
