import {React,useEffect, useState} from 'react';
import '../../App.css';
import {Route, Redirect, useHistory} from 'react-router-dom'
import Modal from 'react-modal';
import { useForm } from "react-hook-form";
import { login, createUser } from "../../functions/users";

/*Signed up successfully modal style*/
const customStyles = {
    content : {
      top                   : '50%',
      left                  : '50%',
      right                 : 'auto',
      bottom                : 'auto',
      marginRight           : '-50%',
      transform             : 'translate(-50%, -50%)',
      backgroundColor       : "#d3f1ef",
      position              : "fixed",
      width: "300px",
      height: "100px",
    }
  };


function Signuppre() {

    const history = useHistory();
    const [modalIsOpen,setIsOpen] = useState(false);
    const [username, setUsername] = useState("");
    
    const { register, handleSubmit } = useForm();
    const onSubmit = async (data) => {
      var reponse = await createUser(data);
      console.log(reponse);
    };

  function Redirect(page){
    history.push("/"+page);
    return <Redirect    to={{
      pathname:'/'+page
        }}/>
  }

  function openModal(){
    setIsOpen(true);
  }

  function closeModal(){
    setIsOpen(false);
  }


  return (
    <div className="Home">
          <form onSubmit={handleSubmit(onSubmit)}>
            <div style={{margin: '25px 0',display: 'flex',flexDirection:'row'}}>
                <div style={{width: '100px'}}>Username</div>
                <div> <input type="text" {...register("username")} /></div>
              
             
            </div>
            <div style={{margin: '25px 0',display: 'flex',flexDirection:'row'}}>
              
              <div style={{width: '100px'}}>Password</div>
              <div><input {...register("password")} type="password" /></div>
            </div>

            <input type="submit" />
          </form>
      <Modal
          isOpen={modalIsOpen}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
        <div className="Modal">
            <div>נרשמת בהצלחה</div>
            <button className="button button1" onClick={()=>{Redirect('Home')}}>חזור לדף הבית</button>
        </div>
        </Modal>
    </div>
  );
}

export default Signuppre;
