import {React,useEffect, useState} from 'react';
import '../../App.css';
import {Route, Redirect, useHistory} from 'react-router-dom'
import Modal from 'react-modal';
import { useForm } from "react-hook-form";
import { createUser } from "../../functions/users";
import ReactDOM from 'react-dom';

/*Signed up successfully modal style*/
const customStyles = {
    content : {
      top                   : '50%',
      left                  : '50%',
      right                 : 'auto',
      bottom                : 'auto',
      marginRight           : '-50%',
      transform             : 'translate(-50%, -50%)',
      backgroundColor       : "#d3f1ef",
      position              : "fixed",
      width: "300px",
      height: "100px",
    }
  };


function SignUpPre() {

    const history = useHistory();
    const [modalIsOpen,setIsOpen] = useState(false);
    const [username, setUsername] = useState(""); 

    const { register, handleSubmit } = useForm();
    
    const onSubmit = async (data) => {
      var up1 = ReactDOM.render(element, document.getElementById('passF'));
      var up2 = ReactDOM.render(element, document.getElementById('passS'));
      console.log(up1);
      console.log(up2);
      if (up1 == up2){
        var reponse = await createUser(data);
        console.log(reponse);
      }
      // var reponse = await createUser(data);
      // console.log(reponse);
    };

  function Redirect(page){

    history.push("/"+page);
    return <Redirect    to={{
      pathname:'/'+page
        }}/>
  }
  function openModal(){
    setIsOpen(true);
}


  function closeModal(){
    setIsOpen(false);
  }

  return (
    <div className="Home">
      <div className="login" >
        <p staly={{marginBottom: '30px'}}>PartnerTAU ברוכים הבאים להרשמה לאתר</p>
        const element = {
        <div className="col" >
        <form onSubmit={handleSubmit(onSubmit)}>
            <div><input className="inputclass" type ="text"></input>שם פרטי</div>
            <div><input className="inputclass" type ="text"></input>שם משפחה</div>      
            <div><input className="inputclass" type ="email" style={{width: '160px'}} {...register("username")} ></input>Email</div>
            <div><input id="passF" className="inputclass" type ="password" maxLength = "10" {...register("password")} ></input>סיסמה</div>
            <div><input id="passS" className="inputclass" type ="password" maxLength = "10"></input>אימות סיסמה</div>
            <button type="submit" onClick={()=>{openModal()}} className="button button1">הירשם</button>
        </form>
        </div>
};
      </div>
      <Modal
          isOpen={modalIsOpen}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
        <div className="Modal">
            <div>נרשמת בהצלחה</div>
            <button className="button button1" onClick={()=>{Redirect('Home')}}>חזור לדף הבית</button>
        </div>
        </Modal>
    </div>
  );
}

export default SignUpPre;
