  //   MailInfo.title = evt.target.value;
  // }

  // function handleTextChange(evt){
  //   MailInfo.message = evt.target.value;
  //   console.log(MailInfo.message);
  // }

  // function handleSubmitForm(e){
  //   e.preventDefault();
  //   axios({
  //     method: "POST",
  //     url:"http://localhost:3001/contactUs",
  //     data:  MailInfo
  //   }).then((response)=>{
  //     if (response.data.status === 'success') {
  //       alert("Message Sent.");
  //       //this.resetForm()
  //       closeModal()
  //     } else if (response.data.status === 'fail') {
  //       alert("Message failed to send.")
  //     }
  //   })
  // }

  return (
    <div className="Home">
      <div className="Top">
      <Grid
        container fixed
        direction="row"
        justify="flex-start"
        alignItems="center"
      >
        {username && username != "" ? (
          <LightTooltip title="התנתק" placement="bottom">
            <IconButton onClick={signOut} >
              <PowerSettingsNewIcon className={classes.button_LoginOut}>
              </PowerSettingsNewIcon>
            </IconButton>
          </LightTooltip>
          ) : (
          <LightTooltip title="התחבר" placement="bottom">
          <IconButton onClick={openModal}>
            <PersonOutlineOutlinedIcon className={classes.button_LoginOut}>
            </PersonOutlineOutlinedIcon>
          </IconButton>
          </LightTooltip>
          )}

          <LightTooltip title="רשימת הבקשות שלי" placement="bottom">
            <IconButton onClick={() =>  Redirect("List")}>
            <ListIcon className={classes.button_List}>
            </ListIcon>
            </IconButton>
          </LightTooltip>

          <LightTooltip title="דף בית" placement="bottom">
          <IconButton onClick={() =>  Redirect("Home")}>
            <HomeOutlinedIcon className={classes.button_Hone}>
            </HomeOutlinedIcon>
          </IconButton>
          </LightTooltip>

        </Grid>

        <Grid
